import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  template: `
    <footer class="text-center mt-4 mb-2">
      © Anurag Bhattarai, {{ currentYear }}
    </footer>
  `,
  standalone: true
})
export class FooterComponent {
  currentYear = new Date().getFullYear();
}
