import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.css'],
  standalone: true,
  imports: [CommonModule, FormsModule]
})
export class CalculatorComponent {
  num1: number = 0;
  num2: number = 0;
  operation: string = 'add';
  result: number | null = null;

  compute() {
    switch (this.operation) {
      case 'add':
        this.result = this.num1 + this.num2;
        break;
      case 'subtract':
        this.result = this.num1 - this.num2;
        break;
      case 'multiply':
        this.result = this.num1 * this.num2;
        break;
      default:
        this.result = null;
    }
  }
}
<div style="max-width:300px; margin:auto; margin-top:20px;">
  <input type="number" [(ngModel)]="num1" placeholder="Enter first number" /><br /><br />
  <input type="number" [(ngModel)]="num2" placeholder="Enter second number" /><br /><br />

  <select [(ngModel)]="operation">
    <option value="add">Add</option>
    <option value="subtract">Subtract</option>
    <option value="multiply">Multiply</option>
  </select><br /><br />

  <button (click)="compute()">Compute</button><br /><br />

  <p *ngIf="result !== null">Result: {{ result }}</p>
</div>
