import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  standalone: true
})
export class HomeComponent { }
<div style="text-align: center; margin-top: 30px;">
  <h2>Welcome to AngularAppByAnurag</h2>
  <img src="assets/anurag.webp" style="border-radius: 1%; width: 650px; height: 650px;" alt="Anurag Bhattarai" />
</div>
<style>
  h2 {
    color: #1976d2;
  }
  @media (max-width: 600px) {
    .home-image {
      width: 100%;
    }
  }
</style>
